solution "MoToFlex"	configurations {"Full", "Headless"}	basedir "Make"	location "Make"	targetdir "bin"	project "Simulator"		kind "ConsoleApp"		language "C++"		files {			"Simulator/**.h", "Simulator/**.c", "Simulator/**.cpp", "Simulator/**.cc",			"shared/**.h", "shared/**.c", "shared/**.cpp", "shared/**.cc",			"DortmundWalkingEngine/Modules/MotionControl/DortmundWalkingEngine/SwingLegController.*",			"DortmundWalkingEngine/Modules/MotionControl/DortmundWalkingEngine/ZMPIPController.*",			"DortmundWalkingEngine/Modules/MotionControl/DortmundWalkingEngine/ZMPGenerator.*",			"DortmundWalkingEngine/Modules/MotionControl/DortmundWalkingEngine/TiltController.*",			"DortmundWalkingEngine/Modules/MotionControl/DortmundWalkingEngine/PatternGenerator.*",			"DortmundWalkingEngine/Modules/MotionControl/DortmundWalkingEngine/CSConverter.*",			"DortmundWalkingEngine/Modules/MotionControl/NaoKinematic.*",			"DortmundWalkingEngine/Tools/Math/Bspline.*",			"DortmundWalkingEngine/Representations/**.h"		}		includedirs {			"ode/include",			"shared",			"DortmundWalkingEngine",			"Simulator"		}				libdirs { "ode/lib" }				defines { "WALKING_SIMULATOR", "dSINGLE" }		flags { "Symbols" }		configuration "Full"			links { "ode", "drawstuff" }					configuration "Headless"			links { "ode" }			defines { "HEADLESS" }					configuration "Windows" 			links { "user32", "winmm", "gdi32", "opengl32", "glu32" }			files { "ode/drawstuff/resources.rc" }			defines { "_CRT_SECURE_NO_WARNINGS", "WIN32" }					configuration "Linux and Full"			links { "GL", "GLU" }				project "EAController"		kind "ConsoleApp"		language "C++"		files {			"EAController/**.h", "EAController/**.c", "EAController/**.cpp", "EAController/**.cc",			"shared/*.h", "shared/*.c", "shared/*.cpp", "shared/*.cc"		}		configuration "not vs2008 and windows"			excludes { "EAController/cmaindividual.*" } 				includedirs {			"shared",			"EAController",			"shark/include",			"Simulator"		}		configuration "not windows or vs2008"			libdirs { "shark/lib" }			links { "shark" }				configuration "Windows"			defines { "_CRT_SECURE_NO_WARNINGS", "WIN32" }					configuration "Linux"			linkoptions { "-Wl,-rpath,shark/lib" }